import { Avatar } from '@mui/material';
import React, { Component } from 'react';
import './Post.css';
import people1 from '../../images/pp3.jpeg';
import edit from '../../images/edit.svg';
import rajat from '../../images/rajat.jpg'
import like from "../../images/love.svg"; 
import comment from "../../images/comment.svg"; 
import share from "../../images/share.svg"; 
import save from "../../images/save.svg"; 

class Post extends Component {
    constructor(props) {
        super(props);
        this.state={
            commentList:[]
        }
    }

    getComment=()=>{
        let data=[
            {
                "userName":"Priyansh",
                "commmentId":"1",
                "timestamp":"12:25",
                "comment":"comment 1"
            }
        ];
        this.setState({commentList:data});
    }

    
    render() { 
        return ( 
            <div>

                <div className='post_container'>

                    {/* Header */}
                    <div className='post_header'>
                        <Avatar className='post_avatar' src={people1}></Avatar>
                        <div className='post_username'>{this.props.userName}</div>
                        <div className='post_edit'><img style={{"position": "absolute","right": "480px" }} src={edit}/></div>
                    </div>

                    {/* Image */}
                    <div className='post_media'>
                        <img src={rajat} width="550px"/>
                    </div>

                    {/* Analytics */}
                    <div>
                        <div className='post_analytics'>
                            <img src={like}  className='post_reactions'/>
                            <img src={comment}  className='post_reactions'/>
                            <img src={share}  className='post_reactions'/>
                            <img src={save} className='post_reaction_save'/>
                        </div>
                        <div className='analytics_likes'>{this.props.imageLikes} Likes</div>
                        <div className='analytics_username_caption'>
                            <div className='analytics_username'>raj_rajat_narula</div>
                            <div className='analytics_caption'>Chaand Taare Sitaare</div>
                        </div>
                    </div>

                    {/* Comment */}
                    <div className='post_comments'>
                        <div style={{"color":"#989898","margin-left":"20px","margin-top":"5px"}} >View All Comments</div>

                        this.state.
                        <div style={{"margin-left":"20px","margin-top":"5px"}} >Comment 1</div>
                        <div style={{"margin-left":"20px","margin-top":"5px"}} >Comment 2</div>
                        <div style={{"margin-left":"20px","margin-top":"5px"}} >Comment 3</div>
                        <div style={{"margin-left":"20px","margin-top":"5px"}} >Comment 4</div>   
                    </div>

                    {/* Write Comment */}
                    <div style={{"display":"flex"}}>
                        <input className='post_comment_bar' type="text" placeholder="Add a comment" />
                        <button className='post_comment_button'>Post</button>    
                    </div> 

                </div>

            </div>
         );
    }
}
 
export default Post;
